function AddComma(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}
function Single(ds, col) {
    if (ds.length === 0) return '';
    return ds[0][col];
}

function SingleDate(ds, col, frmt) {
    var gg = Single(ds, col);
    return DateTime.Format(gg, frmt)
}

function FormatDate(dt, frmt) {
    return DateTime.Format(dt, frmt);
