helpers:
function Single(ds, col){
    if(ds.length === 0) return '';
    return ds[0][col];
}

function SingleDate(ds, col, frmt){
    var gg = Single(ds, col);
     return DateTime.Format(gg, frmt)
}

function FormatDate(dt, frmt){
    return DateTime.Format(dt, frmt);
}

function Sum(itms){
    return itms.length;
}

var DateTime = (function () {
			        return {
			            Format: function () {
			                var token = /d{1,4}|m{1,4}|yy(?:yy)?|([HhMsTt])\1?|[LloSZ]|"[^"]*"|'[^']*'/g,
                                timezone = /\b(?:[PMCEA][SDP]T|(?:Pacific|Mountain|Central|Eastern|Atlantic) (?:Standard|Daylight|Prevailing) Time|(?:GMT|UTC)(?:[-+]\d{4})?)\b/g,
                                timezoneClip = /[^-+\dA-Z]/g,
                                pad = function (val, len) {
                                    val = String(val);
                                    len = len || 2;
                                    while (val.length < len) val = "0" + val;
                                    return val;
                                };

			                // Regexes and supporting functions are cached through closure
			                return function (date, mask, utc) {
			                    //var dF = DateTime.Format;
			                    if (DateTime.IsNewtonFormat(date))
			                        date = date.replace('T', ' ');

			                    // You can't provide utc if you skip other args (use the "UTC:" mask prefix)
			                    if (arguments.length == 1 && Object.prototype.toString.call(date) == "[object String]" && !/\d/.test(date)) {
			                        mask = date;
			                        date = undefined;
			                    }

			                    // Passing date through Date applies Date.parse, if necessary
			                    date = date ? new Date(date) : new Date;
			                    if (isNaN(date)) throw SyntaxError("invalid date");

			                    //mask = String(dF.masks[mask] || mask || dF.masks["default"]);
			                    mask = String(DateTime.masks[mask] || mask || DateTime.masks['default']);

			                    // Allow setting the utc argument via the mask
			                    if (mask.slice(0, 4) == "UTC:") {
			                        mask = mask.slice(4);
			                        utc = true;
			                    }

			                    var _ = utc ? "getUTC" : "get",
                                    d = date[_ + "Date"](),
                                    D = date[_ + "Day"](),
                                    m = date[_ + "Month"](),
                                    y = date[_ + "FullYear"](),
                                    H = date[_ + "Hours"](),
                                    M = date[_ + "Minutes"](),
                                    s = date[_ + "Seconds"](),
                                    L = date[_ + "Milliseconds"](),
                                    o = utc ? 0 : date.getTimezoneOffset(),
                                    flags = {
                                        d: d,
                                        dd: pad(d),
                                        ddd: DateTime.i18n.dayNames[D], // dF.i18n.dayNames[D],
                                        dddd: DateTime.i18n.dayNames[D + 7],
                                        m: m + 1,
                                        mm: pad(m + 1),
                                        mmm: DateTime.i18n.monthNames[m],
                                        mmmm: DateTime.i18n.monthNames[m + 12],
                                        yy: String(y).slice(2),
                                        yyyy: y,
                                        h: H % 12 || 12,
                                        hh: pad(H % 12 || 12),
                                        H: H,
                                        HH: pad(H),
                                        M: M,
                                        MM: pad(M),
                                        s: s,
                                        ss: pad(s),
                                        l: pad(L, 3),
                                        L: pad(L > 99 ? Math.round(L / 10) : L),
                                        t: H < 12 ? "a" : "p",
                                        tt: H < 12 ? "am" : "pm",
                                        T: H < 12 ? "A" : "P",
                                        TT: H < 12 ? "AM" : "PM",
                                        Z: utc ? "UTC" : (String(date).match(timezone) || [""]).pop().replace(timezoneClip, ""),
                                        o: (o > 0 ? "-" : "+") + pad(Math.floor(Math.abs(o) / 60) * 100 + Math.abs(o) % 60, 4),
                                        S: ["th", "st", "nd", "rd"][d % 10 > 3 ? 0 : (d % 100 - d % 10 != 10) * d % 10]
                                    };

			                    return mask.replace(token, function ($0) {
			                        return $0 in flags ? flags[$0] : $0.slice(1, $0.length - 1);
			                    });
			                };
			            }(),
			            masks: {
			                "default": "ddd mmm dd yyyy HH:MM:ss",
			                shortDate: "m/d/yy",
			                mediumDate: "mmm dd, yyyy",
			                longDate: "mmmm d, yyyy",
			                fullDate: "dddd, mmmm d, yyyy",
			                shortTime: "h:MM TT",
			                mediumTime: "h:MM:ss TT",
			                longTime: "h:MM:ss TT Z",
			                isoDate: "yyyy-mm-dd",
			                isoTime: "HH:MM:ss",
			                isoDateTime: "yyyy-mm-dd'T'HH:MM:ss",
			                isoUtcDateTime: "UTC:yyyy-mm-dd'T'HH:MM:ss'Z'",
			                inSysDateTime: "hh:MM TT mmm dd, yyyy",
			                sometime: 'hhMMss',
			                shortDate2: "mm/dd/yyyy",
			                jDPInsysDate: "M dd, yyyy"
			            },
			            i18n: {
			                dayNames: [
                                "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat",
                                "Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"
			                ],
			                monthNames: [
                                "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec",
                                "January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"
			                ]
			            },
			            JSONDate: function (jsonDate, format) {
			                //console.log(jsonDate.match(/\d+/)[0]);

			                var d = new Date(parseInt(jsonDate.substr(6)));

			                return DateTime.Format(d, format);
			            },
			            ShortDate2: function (dateValue) {
			                date = new Date();

			                if (typeof dateValue != 'undefined')
			                    date = dateValue;

			                return DateTime.Format(date, DateTime.masks.shortDate2)
			            },
			            Between: function (sdate, edate, callback, onFinish) {
			                try {
			                    var i = 0;
			                    var d = sdate;
			                    while (true) {
			                        if (d >= edate) {
			                            onFinish();
			                            break;
			                        } else
			                            callback(i, d);

			                        d.setTime(d.getTime() + 86400000);
			                        i++;
			                    }
			                } catch (ex) {
			                    console.error('DateTime.Between', ex);
			                }
			            },
			            ToDate: function (jsonDate) {
			                return new Date(parseInt(jsonDate.substr(6)));
			            },
			            IsNewtonFormat: function (dte) {
			                return /^(\d{4})-(\d{1,2})-(\d{1,2})T(\d{1,2}):(\d{1,2}):(\d{1,2})?.(\d{1,3})?$/.test(dte);
			                //return /^(\d{4})-(\d{1,2})-(\d{1,2})T(\d{1,2}):(\d{1,2}):(\d{1,2})+(.\d{3})?$/.test(dte);
			            }
			        }
			    }())

function get(obj, prop) {
	var parts = prop.split('.'),
		last = parts.pop();

	while ((prop = parts.shift())) {
		obj = obj[prop];

		if (obj == null) {
			return;
		}
	}

	return obj[last];
}

function noop() {
	return '';
}

function groupBy(handlebars) {
	var helpers = {
		/**
		 * @method group
		 * @param {Array} list
		 * @param {Object} options
		 * @param {Object} options.hash
		 * @param {String} options.hash.by
		 * @return {String} Rendered partial.
		 */
		group: function (list, options) {
			options = options || {};

			var fn = options.fn || noop,
				inverse = options.inverse || noop,
				hash = options.hash,
				prop = hash && hash.by,
				keys = [],
				groups = {};

			if (!prop || !list || !list.length) {
				return inverse(this);
			}

			function groupKey(item) {
				var key = get(item, prop);

				if (keys.indexOf(key) === -1) {
					keys.push(key);
				}

				if (!groups[key]) {
					groups[key] = {
						value: key,
						items: []
					};
				}

				groups[key].items.push(item);
			}

			function renderGroup(buffer, key) {
				return buffer + fn(groups[key]);
			}

			list.forEach(groupKey);

			return keys.reduce(renderGroup, '');
		}
	};

	handlebars.registerHelper(helpers);

	return handlebars;
}

handlebars.registerHelper(groupBy(handlebars));
